  if (data.fileinfo && data.fileinfo._id) {
    delete data.fileinfo._id;
  }
  return axios
    .put(path + "/api/userinfo/" + userid, data)
    .then((result) => {
      return result.data;
    })
    .catch((error) => {
      console.error(error);
      throw error;
    });
};

